<section class="featured-blog-section flex-center">
  <div class="container">
    <header class="flex-col">
      <p class="title--1 bold"><?php single_cat_title(); ?></p>
      <div class="divider"></div>
    </header>
    <?php

    while (have_posts()) {
      the_post();

      $urlParams = explode('/', "$_SERVER[REQUEST_URI]");
      $postCategory = $urlParams[2];
      $postSlug = explode('/', get_the_permalink())[4];
      $permaLink = esc_url(site_url("/{$postCategory}/{$postSlug}"));
    ?>
      <div class="main-post flex-row">
        <a href="<?php echo $permaLink ?>">
          <div class="main-post-img">
            <?php the_post_thumbnail('featuredBlogSize'); ?>
          </div>
        </a>
        <div class="main-post--description flex-col">
          <a href="<?php echo $permaLink ?>">
            <h3 class="underline"><?php the_title(); ?></h3>
          </a>
          <a href="<?php echo $permaLink ?>">
            <p class="underline">
              <?php
              echo wp_trim_words(get_the_content(), 19);
              ?>
            </p>
          </a>
          <div class="article-details flex-row">
            <p class="read-time bold" data-blog="<?php echo wp_strip_all_tags(get_the_content(), true); ?>"></p>
            <p class="title--1 bold">&middot;</p>
            <p class="author"><?php the_author(); ?></p>
          </div>
        </div>
      </div>
    <?php
      break;
    }
    ?>
  </div>
</section>
